package me.unlegitdqrk.fakeminecraftserver;

import me.unlegitdqrk.fakeminecraftserver.data.StatusResponse;
import me.unlegitdqrk.fakeminecraftserver.network.ResponderThread;

import javax.imageio.ImageIO;
import javax.xml.bind.DatatypeConverter;
import java.awt.image.BufferedImage;
import java.io.ByteArrayOutputStream;
import java.io.Closeable;
import java.io.File;
import java.io.IOException;
import java.net.ServerSocket;

import static javax.imageio.ImageIO.read;

/**
 * @author UnlegitDqrk
 */

public class FakeMinecraftServer {
    private static StatusResponse response = null;
    private static boolean stopping = false;
    private static ServerSocket server;

    public static final File SERVER_ICON = new File("server-icon.png");

    public static void start() throws Exception {
        System.out.println("Starting server...");
        printCredits();

        response = new StatusResponse(Settings.PROTOCOL_TEXT, Settings.PROTOCOL_VERSION,
                Settings.MAX_PLAYERS, Settings.ONLINE_PLAYERS,
                getDescription(), loadServerIcon());

        addShutdownHook();
        startServer();
    }

    private static void printCredits() {
        System.out.println("ChatConverter class by md_5: Copyright (c) 2012, md_5. All rights reserved.");
        System.out.println("ByteBufUtils class by Spout LLC: Copyright (c) 2013 Spout LLC <http://www.spout.org/>");
    }

    public static StatusResponse getResponse() {
        return response;
    }

    private static String loadServerIcon() throws IOException {
        if (!SERVER_ICON.exists()) {
            System.err.println("No server-icon was found!");
            return "data:image/png;base64,";
        }

        BufferedImage image = read(SERVER_ICON);
        ByteArrayOutputStream baos = new ByteArrayOutputStream();
        ImageIO.write(image, "png", baos);
        return "data:image/png;base64," + DatatypeConverter.printBase64Binary(baos.toByteArray());
    }

    private static Message getDescription() {
        String motd = Settings.MOTD_LINE1 + "\n" + Settings.MOTD_LINE2;

        if (Settings.ENGINE.equalsIgnoreCase("json"))
            return ChatConverter.toJSONChat(ChatConverter.replaceColors(motd).replace("\\n", "\n"));
        else {
            Message description = new Message();
            description.text = ChatConverter.replaceColors(motd).replace("\\n", "\n");
            return description;
        }
    }

    private static void startServer() {
        if (stopping) throw new IllegalAccessError();

        try {
            server = new ServerSocket(Settings.START_PORT, 5);

            while (!server.isClosed()) {
                new ResponderThread(server.accept()).start();
            }

        } catch (Exception exception) {
            if (!stopping) {
                System.err.println("Failed to start server:");
                exception.printStackTrace();
                System.exit(0);
            }
        } finally {
            stopServer();
        }

    }

    private static void addShutdownHook() {
        Runtime.getRuntime().addShutdownHook(new Thread(() -> stopServer()));
    }

    public static void stopServer() {
        stopping = true;

        if (server == null) return;

        System.out.println("Stopping server...");

        safeClose(server);
        server = null;

        System.out.println("Server stopped.");
    }

    public static void safeClose(final Closeable closeable) {
        if (closeable == null) return;

        try {
            closeable.close();
        } catch (Exception ignored) {
        }
    }
}
